var Output_Frequency    = global.get("C2S.Implicit.Output_Frequency",'memoryOnly')||0;
var Power_Factor        = global.get("C2S.Implicit.Power_Factor",'memoryOnly')||0;
var Output_Voltage      = global.get("C2S.Implicit.Output_Voltage", 'memoryOnly') || 0;
var Bus_DC_Volts        = global.get("C2S.Implicit.Bus_DC_Volts", 'memoryOnly') || 0;
var Output_Power        = global.get("C2S.Implicit.Output_Power", 'memoryOnly') || 0;
var Output_Current      = global.get("C2S.Implicit.Output_Current", 'memoryOnly') || 0;
var Elapsed_Run_Time    = global.get("C2S.Implicit.Elapsed_Run_Time", 'memoryOnly') || 0;
var Last_Fault_Code     = global.get("C2S.Implicit.Last_Fault_Code", 'memoryOnly') || 0;
var Elapsed_kWH         = global.get("C2S.Implicit.Drive_Temp_C", 'memoryOnly') || 0;

var MtrCurrent;
var MtrVoltage;

var MaxLimit = global.get("C2S.Speed_Control.MaxLimit")||1;
var Speed = global.get("C2S.Implicit.Output_Frequency",'memoryOnly')||0;
var SpeedPercentage = (Math.abs(Speed) / MaxLimit)*100;

var MtrCtrl = global.get("C2S.Motor_Control.Type") || "IM";

// Corriente y tensión de motor
var Rtx = global.get("C2S.RelTx") || 1;
MtrVoltage = Output_Voltage * Rtx;
MtrCurrent = Output_Current / Rtx;

return[
    { payload: catcherror(Output_Frequency,3)},
    { payload: catcherror(SpeedPercentage,0)},
    { payload: catcherror(Power_Factor,3)},
    { payload: catcherror(Output_Current,3)},
    { payload: catcherror(Bus_DC_Volts,0)},
    { payload: catcherror(MtrVoltage,1)},
    { payload: catcherror(Output_Power,1)},
    { payload: catcherror(Output_Voltage,1)},
    { payload: catcherror(MtrCurrent,1)},
    { payload: catcherror(Elapsed_Run_Time, 1)},
    { payload: catcherror(Last_Fault_Code, 0)},
    { payload: catcherror(Elapsed_kWH, 1)}
]

function catcherror(variable,dec){
    return parseFloat(variable.toFixed(dec));
}

var Chart_Selection = flow.get("PQA.Harmonics_Chart.Selection", "memoryOnly") || "";


var Frecuencia = global.get("PQA.Real_Time_Parameters.Frequency", 'memoryOnly') || 0;
var LN,LL,Corriente,Factor_Protecia,THDV,THDI,Potencia,FP;

if (Chart_Selection == "Linea1") {
    LN = global.get("PQA.Real_Time_Parameters.Line_Voltage_1_2", 'memoryOnly') || 0;
    LL = global.get("PQA.Real_Time_Parameters.Phase_1_Voltage", 'memoryOnly') || 0;
    Corriente = global.get("PQA.Real_Time_Parameters.Total_Phase_A_Current", 'memoryOnly') || 0;
    THDV = global.get("PQA.Harmonics.Power_Quality['THD_V1(V12)']",'memoryOnly')||0;
    THDI = global.get("PQA.Harmonics.Power_Quality.THD_I1",'memoryOnly')||0;
    Potencia = global.get("PQA.Real_Time_Parameters.Phase_A_Power",'memoryOnly')||0;
    FP = global.get("PQA.Real_Time_Parameters.Phase_A_Power_Factor", 'memoryOnly') || 0;

}
else if (Chart_Selection == "Linea2") {
    LN = global.get("PQA.Real_Time_Parameters.Line_Voltage_2_3", 'memoryOnly') || 0;
    LL = global.get("PQA.Real_Time_Parameters.Phase_2_Voltage", 'memoryOnly') || 0;
    Corriente = global.get("PQA.Real_Time_Parameters.Total_Phase_B_Current", 'memoryOnly') || 0;
    THDV = global.get("PQA.Harmonics.Power_Quality['THD_V2(V31)']", 'memoryOnly') || 0;
    THDI = global.get("PQA.Harmonics.Power_Quality.THD_I2", 'memoryOnly') || 0;
    Potencia = global.get("PQA.Real_Time_Parameters.Phase_B_Power", 'memoryOnly') || 0;
    FP = global.get("PQA.Real_Time_Parameters.Phase_B_Power_Factor", 'memoryOnly') || 0;
}
else if (Chart_Selection == "Linea3") {
    LN = global.get("PQA.Real_Time_Parameters.Line_Voltage_3_1", 'memoryOnly') || 0;
    LL = global.get("PQA.Real_Time_Parameters.Phase_3_Voltage", 'memoryOnly') || 0;
    Corriente = global.get("PQA.Real_Time_Parameters.Total_Phase_C_Current", 'memoryOnly') || 0;
    THDV = global.get("PQA.Harmonics.Power_Quality['THD_V3(V23)']", 'memoryOnly') || 0;
    THDI = global.get("PQA.Harmonics.Power_Quality.THD_I3", 'memoryOnly') || 0;
    Potencia = global.get("PQA.Real_Time_Parameters.Phase_C_Power", 'memoryOnly') || 0;
    FP = global.get("PQA.Real_Time_Parameters.Phase_C_Power_Factor", 'memoryOnly') || 0;

}
else if (Chart_Selection == "LineaP") {
    LN = global.get("PQA.Real_Time_Parameters.Average_Phase_Voltage", 'memoryOnly') || 0;
    LL = global.get("PQA.Real_Time_Parameters.Average_Line_Voltage", 'memoryOnly') || 0;
    THDV = global.get("PQA.Harmonics.Power_Quality.THD_avg",'memoryOnly') || 0;
    THDI = global.get("PQA.Harmonics.Power_Quality.THD_Iavg", 'memoryOnly') || 0;
    Corriente = global.get("PQA.Real_Time_Parameters.Average_Phase_Current", 'memoryOnly') || 0;
    Potencia = global.get("PQA.Real_Time_Parameters.Total_System_Power",'memoryOnly')||0;
    FP = global.get("PQA.Real_Time_Parameters.Total_Power_Factor",'memoryOnly') || 0;
}

return [
    { payload: catcherror(LN, 1) + " " + "V" },
    { payload: catcherror(LL, 1) + " " + "V" },
    { payload: catcherror(Corriente, 1) + " " + "A" },
    { payload: catcherror(FP, 2) + " " + "" },
    { payload: catcherror(THDV, 2) + " " + "%" },
    { payload: catcherror(THDI, 2) + " " + "%" },
    { payload: catcherror(Potencia, 1) + " " + "kW" },
    { payload: catcherror(Frecuencia, 2) + " " + "Hz" }
]

function catcherror(variable, dec) {
    return parseFloat(variable.toFixed(dec));
}

var Process_data = global.get("Process_data.DHS",'memoryOnly')||[];

// Protecciones Instrumentación
var THP = global.get("Process_data.AI['Presión de cabeza THP']", 'memoryOnly') || 0;
var THT = global.get("Process_data.AI['Temperatura de cabeza THT']", 'memoryOnly') || 0;

// Sensor de Fondo
var Vibration_X = Process_data["Vibración X"] || "--";
var Vibration_Z = Process_data["Vibración Z"] || "--";

//Protecciones Fondo
var LeakageCurrent = Process_data["Corriente de fuga"] || "--";
var IntakePressure = Process_data["Presión de entrada"] || "--";
var DischargePressure = Process_data["Presión de descarga"] || "--";
var IntakeTemperature = Process_data["Temperatura de entrada"] || "--";
var MotorTemperature = Process_data["Temperatura de motor"] || "--";
var DischargeTemperature = Process_data["Temperatura de descarga"] || "--";

//Sensorless

var PIP = global.get("SENSORLESS.PIP_inferida","memoryOnly")||"--";
var NFD = global.get("SENSORLESS.Nivel_de_fluido_inferido", "memoryOnly") || "--";
var Submergency = global.get("SENSORLESS.Sumergencia_inferida", "memoryOnly") || "--";
var Flow = global.get("SENSORLESS.Caudal_ajustado_con_la_viscosidad", "memoryOnly") || "--";

return [
    { payload: DecPlacces(IntakePressure, 1, "psi") },
    { payload: DecPlacces(DischargePressure, 1, "psi") },
    { payload: DecPlacces(IntakeTemperature, 1, "ºF") },
    { payload: DecPlacces(DischargeTemperature, 1, "ºF") },
    { payload: DecPlacces(MotorTemperature, 1, "ºF") },
    { payload: DecPlacces(Vibration_X, 3, "G") },
    { payload: DecPlacces(Vibration_Z, 3, "G") },
    { payload: DecPlacces(LeakageCurrent, 3, "mA") },
    { payload: DecPlacces(THP, 2,"psi") },
    { payload: DecPlacces(THT, 2,"ºF") },
    { payload: DecPlacces(PIP, 1, "psi") },
    { payload: DecPlacces(NFD, 1, "ft") },
    { payload: DecPlacces(Submergency, 1, "ft") },
    { payload: DecPlacces(Flow, 1, "m^3/s") }
];

function DecPlacces(value, Places, Units) {
    return typeof value == "number" ? parseFloat(value.toFixed(Places)):0;
}

