var protecciones = global.get("Protections")||null;
var statusprot = global.get("Protections","memoryOnly")||null;
var labelprot = ["Voltage", "Load", "BusDC", "THP", "THT", "VibrationGeneral", "CurrentLeakage", "IntakePressure", "DischargePressure", "IntakeTemperature", "MotorTemperature", "DischargeTemperature", "MotorLoad", "DynamicFluidLevel"];
var estadoprot = [];
var i;

evaluarhab();
if (statusprot!=null)evalstatus();

msg.payload = [ {'protec': "<font size = 4px>Tension",'condic': estadoprot[0]},
                {'protec': "<font size = 4px>Corriente VSD",'condic': estadoprot[1]},
                {'protec': "<font size = 4px>Corriente de motor",'condic': estadoprot[12]},
                {'protec': "<font size = 4px>Bus DC",'condic': estadoprot[2]},
                {'protec': "<font size = 4px>Presión THP",'condic': estadoprot[3]},
                {'protec': "<font size = 4px>Temperatura THT",'condic': estadoprot[4]},
                {'protec': "<font size = 4px>Vibración",'condic': estadoprot[5]},
                {'protec': "<font size = 4px>Corriente de fuga",'condic': estadoprot[6]},
                {'protec': "<font size = 4px>Presión de entrada",'condic': estadoprot[7]},
                {'protec': "<font size = 4px>Presión de descarga",'condic': estadoprot[8]},
                {'protec': "<font size = 4px>Temperatura de entrada",'condic': estadoprot[9]},
                {'protec': "<font size = 4px>Temperatura de motor",'condic': estadoprot[10]},
                {'protec': "<font size = 4px>Temperatura de descarga",'condic': estadoprot[11]},
                { 'protec': "<font size = 4px>Nivel de fluido dinámico", 'condic': estadoprot[12] },
    ];
return msg;
function evaluarhab(){
    for(i=0;i<13;i++){
        var acu=0;
        if(protecciones!=null){
            if (typeof protecciones[labelprot[i]] !='undefined'){
                if (typeof protecciones[labelprot[i]].HH != 'undefined'){
                    if(protecciones[labelprot[i]].HH.EN == true)
                        acu++;
                }
                if (typeof protecciones[labelprot[i]].H != 'undefined') {
                    if(protecciones[labelprot[i]].H.EN == true)
                        acu++;
                }
                if (typeof protecciones[labelprot[i]].L != 'undefined') {
                    if(protecciones[labelprot[i]].L.EN == true)
                        acu++;
                }
                if (typeof protecciones[labelprot[i]].LL != 'undefined') {
                    if(protecciones[labelprot[i]].LL.EN == true)
                        acu++;
                }
                if(acu>0)
                    estadoprot.push("<font size = 4px><font color = green>Normal");
                else
                    estadoprot.push("<font size = 4px><font color = gray>-----");
            }
            else
                estadoprot.push("<font size = 4px><font color = gray>-----");
        }
        else
            estadoprot.push("<font size = 4px><font color = gray>-----");
    }
}

function evalstatus(){
    for(i=0;i<13;i++){
        if(statusprot!=null){
            if(estadoprot[i] == "<font size = 4px><font color = green>Normal"){
                if (typeof statusprot[labelprot[i]] !='undefined'){
                    if(statusprot[labelprot[i]].H == true)
                        estadoprot[i] = "<font size = 4px><font color = orange><b>Alarma H<b>";
                    if(statusprot[labelprot[i]].L == true)
                        estadoprot[i] = "<font size = 4px><font color = orange><b>Alarma L<b>";
                    if(statusprot[labelprot[i]].HH == true)
                        estadoprot[i] = "<font size = 4px><font color = red><b>Disparo HH<b>";
                    if(statusprot[labelprot[i]].LL == true)
                        estadoprot[i] = "<font size = 4px><font color = red><b>Disparo LL<b>";
                }
            }
        }
    }
}

