var indexList   = context.get("indexList","memoryOnly")||[];
var rcList      = context.get("rcList","memoryOnly")||[];
var sum         = 0;

// Indexar data
if(indexList.indexOf(msg.parameter)== -1){
    indexList.push(msg.parameter);
    rcList.push(msg.rc.code == null ? 1 : msg.rc.code);
}

// Reiniciar Listas
if(msg.payload == "reset"){

    // Sumar elementos de la lista rcList
    rcList.forEach(element => sum=sum+element);
    node.status({fill:"green",shape:"dot",text:sum});

    // Limpiar contexto
    context.set("indexList",[],"memoryOnly");
    context.set("rcList",[],"memoryOnly");

    if(sum != 0){
        msg.payload = "Error de escritura";

    }
    else{
        msg.payload = "Escritura exitosa";
    }
    return msg;
}

// Actualizar contexto
context.set("indexList",indexList,"memoryOnly");
context.set("rcList",rcList,"memoryOnly");
return;

