var Corriente = flow.get('Filtro_armonicos.Corriente_bypass')||0;
var payload = msg.payload;
msg.payload = TON(parseFloat(msg.payload) >= Corriente ? true:false,msg.delay);
node.status({fill:msg.payload === true? 'red':'green',shape:"dot",text: parseFloat(payload)+" >= "+Corriente });
return msg;

function TON(val,delay){
    var Ant = context.get("Anterior")||false;
    // Actualizar el contexto
    context.set("Anterior", val);
    //Flanco de subida
    if(val === true && Ant === false){
        context.set("RefTime", Date.now());
    }
    else if(val === true && Ant === true){
        if(Date.now() - context.get("RefTime") >= delay*1000){
            return true;
        }
    }
    return false;
}

