if (!Comm) {
    msg.payload = "Falla de comunicación drive";
}
else if ((delay != null) && (delay > 0)) {
    msg.payload = "Re-arranque en: " + parseInt(delay / 1000) + " s";
    flow.set("Rearranque_delay", delay - 500, 'memoryOnly');
}
else if (Fault) {
    msg.payload = "FALLA de variador!";
}
else if (Prot) {
    msg.payload = "FALLA de protección!";
}
else if (FallaGeneral) {
    msg.payload = "Falla general";
}
else if (CommSts) {
    msg.payload = "Falla communicación I/O";
}
else if (Alarm) {
    msg.payload = "ALARMA!";
}
else if (Active) {
    if (Accel) {
        msg.payload = "ACTIVO Acelerando";
    }
    else if (Decel) {
        msg.payload = "ACTIVO Desacelerando";
    }
    else {
        msg.payload = "ACTIVO";
    }
}
else {
    msg.payload = "DETENIDO";
}
node.status({ fill: "green", shape: "dot", text: Fault });

return msg;

