function Float2Int(payload) {
    const getHex = i => ('00' + i.toString(16)).slice(-2);
    var view = new DataView(new ArrayBuffer(4)),
        result;
    view.setFloat32(0, payload);
    result = Array
        .apply(null, { length: 4 })
        .map((_, i) => getHex(view.getUint8(i)))
        .join('');
    return parseInt(result, 16);
}
