var Vibration_Z = typeof Process_data["Vibración Z"] != 'undefined' ? Process_data["Vibración Z"] : 0;

//Protecciones Fondo
var Vibration_Gral = Vibration_X >= Vibration_Z ? Vibration_X : Vibration_Z;
var LeakageCurrent = typeof Process_data["Corriente de fuga"] != 'undefined' ? Process_data["Corriente de fuga"] : 0;
var IntakePressure = typeof Process_data["Presión de entrada"] != 'undefined' ? Process_data["Presión de entrada"] : 0;
var DischargePressure = typeof Process_data["Presión de descarga"] != 'undefined' ? Process_data["Presión de descarga"] : 0;
