var Decel = (global.get("C2S.Drive_Status.Decel", "memoryOnly") || 0) == 1 ? true : false;

// Setpoints
var Prot = env.get('protection')||"test";
var querySpHH   = "Protections."+Prot+".HH.Setpoint";
var querySpH    = "Protections."+Prot+".H.Setpoint";
var querySpL    = "Protections."+Prot+".L.Setpoint";
var querySpLL   = "Protections."+Prot+".LL.Setpoint";

var HHSetpoint  = global.get(querySpHH)||0;
var HSetpoint   = global.get(querySpH)||0;
var LSetpoint   = global.get(querySpL)||0;
var LLSetpoint  = global.get(querySpLL)||0;

// Enablers
var queryEnHH   = "Protections."+Prot+".HH.EN";
var queryEnH    = "Protections."+Prot+".H.EN";
var queryEnL    = "Protections."+Prot+".L.EN";
var queryEnLL   = "Protections."+Prot+".LL.EN";

var HH_EN       = global.get(queryEnHH)||false;
var H_EN        = global.get(queryEnH)||false;
var L_EN        = global.get(queryEnL)||false;
var LL_EN       = global.get(queryEnLL)||false;

// Death times
var queryDtHH   = "Protections."+Prot+".HH.DT";
var queryDtH    = "Protections."+Prot+".H.DT";
var queryDtL    = "Protections."+Prot+".L.DT";
var queryDtLL   = "Protections."+Prot+".LL.DT";

var HHDeathTime = global.get(queryDtHH)||0;
var HDeathTime  = global.get(queryDtH)||0;
var LDeathTime  = global.get(queryDtL)||0;
var LLDeathTime = global.get(queryDtLL)||0;

// Value
var CurrentValue = msg.payload;

if (CurrentValue >= HHSetpoint && HH_EN === true) {
    msg.disp = true;
    msg.alrm = false;
    msg.message = "Trigger by HH Protection";
    msg.delay = HHDeathTime * 1000;
    msg.state = 4;
}
else if (CurrentValue >= HSetpoint && H_EN === true) {
    msg.disp = false;
    msg.alrm = true;
    msg.message = "Alarm by H Protection";
    msg.delay = HDeathTime * 1000;
    msg.state = 2;
}
else if (CurrentValue <= LLSetpoint && LL_EN === true && Active && !Accel && !Decel) {
    msg.disp = true;
    msg.alrm = false;
    msg.message = "Trigger by LL Protection"
    msg.delay = LLDeathTime * 1000;
    msg.state = 3;
}
else if (CurrentValue <= LSetpoint && L_EN === true && Active && !Accel && !Decel) {
    msg.disp = false;
    msg.alrm = true;
    msg.message = "Alarm by L Protection";
    msg.delay = LDeathTime * 1000;
    msg.state = 1;
}
else {
    msg.disp = false;
    msg.alrm = false;
    msg.message = "Normal condition";
    msg.reset = true;
    msg.state = 0;
}
return msg;

