function Escalizar(b,value){
    var a = [0,1];
    // Mapeo
    if(b !== null && typeof b[0] == 'number' && typeof b[1] == 'number' ){
        var t = b[0] + ((value - a[0])*(b[1] - b[0])) / (a[1]-a[0]);
        // Saturación
        if(t > b[1]){    
            t = b[1];
        }
        else if(t < b[0]){ 
            t = b[0];
        }
        // Asignar payload
        return parseFloat(t.toFixed(2));
    }
    else {
        // Asignar payload
        return 0;
    }
}

