var PIDEnable       = flow.get("PID.Enable")||false;
var CEnable         = global.get("Protections.Load.H.Setpoint")||0;
var VEnable         = global.get("Protections.VibrationGeneral.H.Setpoint")||0;
var MEnable         = global.get("Protections.MotorTemperature.H.Setpoint")||0;

var CEnableLimit    = flow.get("PID.Activation.Current")||0;
var VEnableLimit    = flow.get("PID.Activation.Vibration")||0;
var MEnableLimit    = flow.get("PID.Activation.Temp")||0;

// Current
var Current = global.get("C2S.Implicit.Output_Current","memoryOnly")||0;

// Vibration
var VibrationX      = DHData["Vibración X"]|| 0;
var VibrationZ      = DHData["Vibración Z"]|| 0;
var Vibration       = VibrationX >= VibrationZ ? VibrationX:VibrationZ;

// Mtr Temp
var MtrTemp         = DHData["Temperatura de motor"]||0;

var CurrentActivation   = flow.get("PID.Activation.Rules[0]")||false;
var VibrationActivation = flow.get("PID.Activation.Rules[1]")||false;
var MotorTempActivation = flow.get("PID.Activation.Rules[2]")||false;

//Rules
var CEnableRule     = CurrentActivation     == true ? (Current   <= CEnable*CEnableLimit/100 ? true:false)  :true;
var VEnableRule     = VibrationActivation   == true ? (Vibration <= VEnable*VEnableLimit/100 ? true:false)  :true;
var MtrTempRule     = MotorTempActivation   == true ? (MtrTemp   <= MEnable*MEnableLimit/100 ? true:false)  :true;

// Activation Rule
var ActivationRule  = PIDEnable && CEnableRule && VEnableRule && MtrTempRule;

// Dead Band Rule
var setpoint = 0;   var Min = 0;    var Max = 0;

if(PIDType == "Corriente"){
    setpoint = flow.get("PID.Corriente.setpoint")||0;
    Min = setpoint - DeadBand;
    Max = setpoint + DeadBand;
    if(msg.payload >= Min && msg.payload <= Max ){
        var DeadBandRule = false;
    }
    else{
        var DeadBandRule = true;
    }
}
else if(PIDType == "Presion"){
    setpoint = flow.get("PID.Presion.setpoint")||0;
    Min = setpoint - DeadBand;
    Max = setpoint + DeadBand;
    if(msg.payload >= Min && msg.payload <= Max ){
        DeadBandRule = false;
    }
    else{
        DeadBandRule = true;
    }
}

// Last asingation
msg.enable = (ActivationRule && DeadBandRule);
flow.set("PID.enable",msg.enable,"memoryOnly");
node.status({fill:"green",shape:"dot",text:"PID:"+PIDEnable+" Corriente:"+CEnableRule+" Vibración:"+VEnableRule + " Temp. Mtr:"+MtrTempRule});
return msg;

