function filtrarportiempo(selectortiempo, eventos){
    var i, a=[], aux,flag;
    var DT = new Date(); // Instancia que representa un único momento en el tiempo (ms)
    var date = new Date(eventos[0].Fecha + "," + eventos[0].Hora);
    aux = (DT.getTime() - date.getTime()) / (1000 * 60 * 60 * 24);//(1000*60*60*24);

    for (i = 0; i < eventos.length; i++) {
        flag = false
        if(selectortiempo == "all") flag=true;
        else{
            var date = new Date(eventos[i].Fecha + "," + eventos[i].Hora);
            aux = (DT.getTime() - date.getTime())/(1000*60*60*24);
        }
        if (selectortiempo == "lastday" && aux <= 1) flag = true;
        if (selectortiempo == "lastweek" && aux <= 7) flag = true;
        if (selectortiempo == "lastmonth" && aux <= 30) flag = true;
        if (flag) a.push({ "ID": eventos[i].ID, "StartingGroup": eventos[i].StartingGroup, "Fecha": eventos[i].Fecha, "Hora": eventos[i].Hora, "Milisegundos": eventos[i].Milisegundos, "VSagVSwell": eventos[i].VSagVSwell, "EstadoC2S": eventos[i].EstadoC2S });
    }
    return a;
}

function filtrarporestado(selectorestado, eventos){
    var i; var a = [];var flag;
    for (i = 0; i < eventos.length; i++) {
        flag = false
        if (selectorestado == "all") flag = true;
        if (selectorestado == "active" && eventos[i].EstadoC2S=="Activo") flag = true;
        if (flag) a.push({ "ID": eventos[i].ID, "StartingGroup": eventos[i].StartingGroup, "Fecha": eventos[i].Fecha, "Hora": eventos[i].Hora, "Milisegundos": eventos[i].Milisegundos, "VSagVSwell": eventos[i].VSagVSwell, "EstadoC2S": eventos[i].EstadoC2S });
    }
