PMP         = 4500; //FT
PIP_Sensor  = 750; //psi
PCasing     = 0;   //psi
*/
var Analog = global.get("DownHoleData","memoryOnly")||[];

// ESTADO MECANICO
var PMP             = global.get("IWOC.ESTADO_MECANICO.PMP")||4500; //Presión del tubo 
var Prof_Discharge  = global.get("IWOC.ESTADO_MECANICO.Profundidad_de_descarga")||0;
var Prof_Sensor     = global.get("IWOC.ESTADO_MECANICO.Prof_Sensor")||4030;
var Prof_Intake     = global.get("IWOC.ESTADO_MECANICO.Profundidad_de_entrada")||4000;

// PROPIEDADES DE FLUIDO
var API             = global.get("IWOC.PROPIEDADES_DE_FLUIDO.API")||30;
var T_fluido        = global.get("IWOC.PROPIEDADES_DE_FLUIDO.Temperatura_de_fondo")||160;
var Dens_Agua       = global.get("IWOC.PROPIEDADES_DE_FLUIDO.Densidad_del_agua")||8.33;

// PRUEBA DE PRODUCCION
var Pwf_P           = global.get("IWOC.PRUEBA_DE_PRODUCCION.PWF_Prueba")||750;
var BSW             = global.get("IWOC.PRUEBA_DE_PRODUCCION.BSW_Prueba")||98; //Relación Agua-Petroleo
var Q_p             = global.get("IWOC.PRUEBA_DE_PRODUCCION.Q_Prueba")||2300;
var Vel_P           = global.get("IWOC.PRUEBA_DE_PRODUCCION.Velociad_de_prueba")||0;

// DATOS PARA IPR
var Py              = global.get("IWOC.DATOS_PARA_IPR.Presion_de_yacimiento")||2500;
var Pcrit           = global.get("IWOC.DATOS_PARA_IPR.PWF_Critica")||1050;
var Pb              = global.get("IWOC.DATOS_PARA_IPR.Presion_de_burbuja")||1200;

//var PIP_Sensor      = Analog.DHS["Presión de entrada"]||Analog.AI["Presión de entrada"];
var PIP_Sensor = 750; //psi / Presión de la entrada de la bomba 
var PCasing         = 0;

var AxisX = [];
var AxisY = [];
/*
    Paso No. 1: Se halla Gradiente de Presión corregido por 
    temperatura y %BSW a partir de la Gravedad Específica (GE)
    del crudo usando gravedad API del crudo.
*/

var GE = 141.5/(131.5 + API); //Corrección de la Gravedad Especifica del crudo

/*
    Paso No. 2: Hallar valores de alfa y beta según la gravedad 
    específica (GE) siguiendo lo que indica la tabla abajo:
*/

if(GE<0.63){
    var Alfa = 0.00097;
    var Beta = -0.0000004;
}
else if((GE<0.78) && (GE>=0.63)){
    var Alfa = (0.00097+(GE-0.63))*((0.00075-0.00097)/(0.78-0.63));
    var Beta = (0.0000004 + (GE-0.63))*(0.0000004/(0.78-0.63));
}
else if((GE<0.85)&&(GE>=0.78)){
    var Alfa = (0.00075+(GE-0.75))*((0.00068-0.00075)/(0.85-0.78));
    var Beta = (0 + (GE-0.75))*(0.0000001/(0.85-0.78));
}   
else if((GE<0.95)&&(GE>=0.85)){
    var Alfa = (0.00068+(GE-0.85))*((0.00066-0.00068)/(0.95-0.85));
    var Beta = (0.0000001 + (GE-0.85))*((0.0000001-0.0000003)/(0.95-0.85));
}   
else if(GE>=0.95){
    var Alfa = 0.00066;
    var Beta = -0.0000003;
}

//GE_o  = Gravedad específica de aceite ajustada 
var GE_o = GE - (Alfa*(T_fluido-60)) + (Beta*Math.pow((T_fluido-60), 2)); // Temperatura de fondo - Temperatura ambiente (60°) 

/*
    Paso No. 3: Hallar el Gradiente de presión corregido
*/
var Grv_TFondoBSW = (((100 - BSW) / 100) * GE_o) + ((BSW / 100) * (Dens_Agua / 8.345)) 
var VP = 0.433 * Grv_TFondoBSW // Gradiente de presión


/*
    Paso No. 4: Cálculo de PIP en iWOC según tres escenarios

    •	Escenario No. 1: Cálculo de Variables en Función del Sensor de Fondo

        Cálculo de PIP, PWF y Sumergencia líquida cuando la Fuente de Medición es un sensor de fondo.
*/

    var PIP_real    = PIP_Sensor + ((Prof_Intake - Prof_Sensor)*VP);
    var Pwf         = PIP_Sensor + ((PMP - Prof_Sensor)*VP);
    var Sum_Liq     = (PIP_real- PCasing)/ VP;

/*
    Paso No. 5: Cálculo de Caudales
*/    
/* Yacimiento Subsaturado*/
if(Py>Pb){
    if(Pwf>Pb){
        var IP = Q_p/(Py-Pwf_P);
        //node.send()
    }
    else{
        IP = Q_p/(Py - Pb + (Pb/1.8)*(1-(0.2*(Pwf/Pb))-0.8*(Math.pow((Pwf/Pb),2))))
    }

    var Qb      = IP * (Py - Pb);
    var Qmax    = Qb + (IP*Pb/1.8);
    var N       = 80;

    for (var i = 80; i >= 0;i--){
        var Pwf_G = (Py*i/N);
        if(Pwf_G>Pb){
            var Qg = IP *(Py- Pwf_G);
        }
        else{       
            Qg = Qb + (Qmax - Qb) * (1 - (0.2 * (Pwf_G / Pb)) - (0.8 * ((Pwf_G / Pb) * (Pwf_G / Pb))));
        }
        AxisX.push(Qg);
        AxisY.push(Pwf_G);
    }
    /*
        Cálculo Caudal actual Qo
    */
    if(Pwf>Pb){
        var Q0 = IP *(Py- Pwf);    
    }
    else{       
        var Q0 = Qb + (Qmax - Qb) * (1 - (0.2 * (Pwf / Pb)) - (0.8 * ((Pwf / Pb) * (Pwf / Pb))));
    }
}


msg.payload = {
    'AxisX' : AxisX,
    'AxisY' : AxisY,
    'Pcrit' : Pcrit,
    'Q0'    : Q0,
    'Pwf'   : Pwf
};


return msg;

