// ESTADO MECANICO
var PMP             = global.get("IWOC.ESTADO_MECANICO.PMP")||2842; //Punto medio de perforados 
var Prof_Discharge  = global.get("IWOC.ESTADO_MECANICO.Profundidad_de_descarga")||0;
var Prof_Sensor     = global.get("IWOC.ESTADO_MECANICO.Prof_Sensor")||2842;
var Prof_Intake     = global.get("IWOC.ESTADO_MECANICO.Profundidad_de_entrada")||2586.73;

// PROPIEDADES DE FLUIDO
var API             = global.get("IWOC.PROPIEDADES_DE_FLUIDO.API")||13.8;
var T_fluido        = global.get("IWOC.PROPIEDADES_DE_FLUIDO.Temperatura_de_fondo")||150;
var Dens_Agua       = global.get("IWOC.PROPIEDADES_DE_FLUIDO.Densidad_del_agua")||8.33;

// PRUEBA DE PRODUCCION
var Pwf_P           = global.get("IWOC.PRUEBA_DE_PRODUCCION.PWF_Prueba")||750;
var Q_p             = global.get("IWOC.PRUEBA_DE_PRODUCCION.Q_Prueba")||2300;
var Vel_P           = global.get("IWOC.PRUEBA_DE_PRODUCCION.Velociad_de_prueba")||0;

// DATOS PARA IPR
var Py = global.get("IWOC.DATOS_PARA_IPR.Presion_de_yacimiento") || 1110.14;
var Pcrit           = global.get("IWOC.DATOS_PARA_IPR.PWF_Critica")||1050;
var Pb              = global.get("IWOC.DATOS_PARA_IPR.Presion_de_burbuja")||130;

//DATOS PARA SENSORLESS
var qo              = global.get("IWOC.DATOS_PARA_SENSORLESS.Caudal_de_Aceite") || 132;
var qw              = global.get("IWOC.DATOS_PARA_SENSORLESS.Caudal_de_Agua") || 3422;
var GE_w            = global.get("IWOC.DATOS_PARA_SENSORLESS.Gravedad_especifica_del_agua") || 1;
//var frec            = global.get("IWOC.DATOS_PARA_SENSORLESS.Frecuencia_de_trabajo") || 55; // Frecuencia de trabajo (Hz)
var U_ref           = global.get("IWOC.DATOS_PARA_SENSORLESS.Frecuencia_de_referencia") || 60; //Frecuencia de referencia(Hz)
var N_etapas        = global.get("IWOC.DATOS_PARA_SENSORLESS.N_etapas") ||  400; 
var diam            = global.get("IWOC.DATOS_PARA_SENSORLESS.Diametro_de_tuberia") || 0.25; 
var THP             = THPInstrument>=10? THPInstrument: (global.get("IWOC.DATOS_PARA_SENSORLESS.Presion_en_el_tubing") || 175); 
var l1              = global.get("IWOC.DATOS_PARA_SENSORLESS.Longitud_del_tubing") || 2556.73; //Longitud del tubing por encima de la bomba
//var V_motor         = global.get("IWOC.DATOS_PARA_SENSORLESS.Voltaje_motor") || 1303;
//var A_motor         = global.get("IWOC.DATOS_PARA_SENSORLESS.Corriente_motor") || 42.8; 
var lcable          = global.get("IWOC.DATOS_PARA_SENSORLESS.Longitud_cable") || 2753.94;
//var FP              = global.get("IWOC.DATOS_PARA_SENSORLESS.Factor_potencia") || 0.95;
var POT_Placa       = global.get("IWOC.DATOS_PARA_SENSORLESS.Potencia_de_la_placa") || 1776;
var N_sellos        = global.get("IWOC.DATOS_PARA_SENSORLESS.Num_sellos") || 2;
var GE_g            = global.get("IWOC.DATOS_PARA_SENSORLESS.Gravedad_Especifica_Gas") || 0;
var Fac_compres     = global.get("IWOC.DATOS_PARA_SENSORLESS.Factor_Compresibilidad") || 0;

var PIP_Sensor      = 750; //psi / Presión de la entrada de la bomba 
var PCasing         = PCasingInstrument>=8 ? PCasingInstrument : 0.1; //Presión en el Casing 

/*
    Paso No. 1: Se halla Gradiente de Presión corregido por 
    temperatura y %BSW a partir de la Gravedad Específica (GE)
    del crudo usando gravedad API del crudo.
*/

var GE = (141.5) / (131.5 + (API)); //Corrección de la Gravedad Especifica del crudo

/*
    Paso No. 2: Hallar valores de alfa y beta según la gravedad 
    específica (GE) siguiendo lo que indica la tabla abajo:
*/

if(GE<0.63){
    var Alfa = 0.00097;
    var Beta = -0.0000004;
}
else if((GE<0.78) && (GE>=0.63)){
    var Alfa = 0.00097+(GE-0.63)*((0.00075-0.00097)/(0.78-0.63));
    var Beta = 0.0000004 + (GE-0.63)*(0.0000004/(0.78-0.63));
}
else if((GE<0.85)&&(GE>=0.78)){
    var Alfa = 0.00075+(GE-0.75)*((0.00068-0.00075)/(0.85-0.78));
    var Beta = 0 + (GE-0.75)*(0.0000001/(0.85-0.78));
}   
else if((GE<0.95)&&(GE>=0.85)){
    var Alfa = 0.00068 + (GE-0.85) * ((0.00066 - 0.00068)/(0.95-0.85));
    var Beta = 0.0000001 + (GE-0.85)*((0.0000001-0.0000003)/(0.95-0.85));
}   
else if(GE>=0.95){
    var Alfa = 0.00066;
    var Beta = -0.0000003;
}

//GE_o  = Gravedad específica de aceite ajustada 
var GE_o = GE - (Alfa * (T_fluido - 60)) + (Beta * Math.pow((T_fluido - 60), 2)); // Temperatura de fondo - Temperatura ambiente (60°)

/*
    Paso No. 3: Hallar el Gradiente de presión corregido
*/
//var Grv_TFondoBSW = (((100 - BSW) / 100) * GE_o) + ((BSW / 100) * (Dens_Agua / 8.345)) 
//var VP = 0.433 * Grv_TFondoBSW // Gradiente de presión

//---------------------------------------------------------------------------------------------------------------------
//CALCULO DE GAS

/*
    Paso No. 4: Calculo de la Gravedad Especifica del gas 
*/


GE_g = 0.00091 * (T_fluido + 460) - (0.0125 * API);

/*
    Paso No. 5: Se calcula el RS y GOR con la ecuación de Standing
                GOR = Relación gas – aceite (SCF/STB)
                RS =  Relación gas disuelto – aceite (SCF/STB)
*/

var GOR = GE_g * Math.pow(((Py / 18) * ((Math.pow(10, (0.0125 * API)) / Math.pow(10, (0.00091 * T_fluido))))),1.2048);
var RS = GE_g * (Math.pow((Pb / (18 * (Math.pow(10, GE_g)))), 1.2048));

/*
    Paso No. 6: Se determinan los factores volumétricos.
                Bo = Factor volumétrico de aceite (Bbl/STB)
                Bg = Factor volumétrico de gas (ft3/SCF)
                Fac_compres  = Factor de compresibilidad
*/
var Bo = 0.972 + 0.000147 * (Math.pow((RS * (Math.pow((GE_g / GE_o), 0.5)) + 1.75 * (parseInt(T_fluido))), 1.175));


    //Factor de comprensibilidad 
var a = -0.000720704;
var b =  0.008654874;
var c = -0.009919065;
var d = -0.124590401;
var e =  1;
var P_pc = 677 + (15 * GE_g) - (37.5 * (Math.pow(GE_g,2))) // Presión pseudocrítica

Fac_compres = a * (Math.pow(((Py + 14.7) / (P_pc* GE_g)),4)) + b * (Math.pow(((Py + 14.7) / (P_pc * GE_g)), 3)) + c * (Math.pow(((Py + 14.7) / (P_pc * GE_g)), 2)) + d * ((Py + 14.7) / (P_pc * GE_g)) + e;

var Bg = 5.04 * Fac_compres * ((parseInt(T_fluido) + 460)/Py);

/*
    Paso No. 7: Se calculan los volúmenes de gas total, gas en solución y gas libre
                V_Tg = Volumen de gas total
                V_Sg = Volumen de gas en solución 
                V_Fg = Volumen de gas libre 
*/
var V_Tg = qo * (GOR/1000);
var V_Sg = qo * (RS / 1000);
var V_Fg = V_Tg - V_Sg ; 

/*
    Paso No. 8: Se calculan los demás volúmenes(aceite, gas, agua y total)
                Vo_int = Volumen de aceite en fondo (Bbl)
                Vg = Volumen de gas libre en fondo (ft3)
                Vw = Volumen de agua (Bbl)
                Vt = Volumen total de fluidos (Bbl)
                BSW = Relación Petroleo - Agua
                qt = Caudal total
*/
var Vo_int = qo * Bo;
var Vg = V_Fg * Bg;
var qt = (parseInt(qo) + parseInt(qw));
var BSW = (parseInt(qw) / qt) ;
var Vw = qt * BSW
var Vt = Vo_int + Vg + Vw; 

/*
    Paso No. 9: Cálculo de la masa total de fluido, GE de la mezcla y densidad
                TMPF = Masa total de los fluidos (lb)
                GE_mix = Gravedad específica de la mezcla
                dens_mix = Densidad de la mezcla (ppg)                
*/
var TMPF = (qo * GE_o + qt * BSW * GE_w)* 62.4 * 5.6146 + (GOR * qo * GE_g * 0.0752); // GE_w gravedad especifica del agua
var GE_mix = TMPF / (Vt * 5.6146 * 62.44);
var dens_mix = GE_mix * 62.4 ; 

/*
    Paso No. 10: Cálculo de la viscosidad. Se utilizan una de las tres correlaciones dependiendo del API del crudo
                Egbogah-Ng
                Beggs - Robinson
                Kartoatmodjo - Schmidt
*/
if ((API >= 5) && (API < 16)) {
    var z = 0;
    var y = 2.06492 - (0.0179 * API) - (0.70226 * (Math.log10(T_fluido)));
    var x = Math.pow(10, y);
    var visc = (Math.pow(10, x)) - 1;
} else if ((API >= 16) && (API < 40)) {
    var z = 3.0324 - (0.02023 * API);
    var y = Math.pow(10, z);
    var x = y * Math.pow((T_fluido), (-1.163));
    var visc = (Math.pow(10, x)) - 1;
} else if ((API >= 40) && (API <= 59)) {
    var z = 0;
    var y = 0;
    var x = (5.7526 * (Math.log10(T_fluido))) - 26.9718;
    var visc = (16) * (Math.pow(10, 8)) * (Math.pow(T_fluido, -2.8177)) * (Math.pow((Math.log10(API)), x));
}

 var visc1 = visc / 1000;

/*
    Paso No. 11: Corrección con viscosidad (la viscosidad debe ingresarse en cp/1000)
*/
var Cq = 1 - 2.6266 * visc1 + 6.0032 * (Math.pow(visc1, 2)) - 6.8104 * (Math.pow(visc1, 3)) + 2.7944 * (Math.pow(visc1 , 4));
var Ch = 1 - 0.03 * visc1;
var Cp = 1 + 3.9042 * visc1 - 9.9306 * (Math.pow(visc1, 2)) + 11.091 * (Math.pow(visc1, 3)) - 4.4376 * (Math.pow(visc1 , 4));

/*
    Paso No. 12: Calculo de caudales. Para el cálculo se necesita hacer una corrección del caudal total a partir del Uqaj
                qt = Caudal total
                qo = Caudal de aceite (Bbl/d)
                qw = Caudal de agua (Bbl/d)
                qxo = Caudal total (m3/d)
                frec = Frecuencia de trabajo (Hz)
                U_ref = Frecuencia de referencia (Hz)
                U_qaj = Frecuencia ajustada (Hz)
                q_xxo = Caudal total ajustado (m3/d)

*/
var qxo = qt * 0.159;
var U_qaj = frec / U_ref;
var q_xxo = qxo * U_qaj; 

/*
    Paso No. 13: Se calcula un HEADo EFFo y POWERo(curvas características de la bomba) 
                HEAD_o = Head inicial
                EFF_o = Eficiencia inicial
                POWER_o = Potencia inicial
                HEAD = Head ajustado
                POWER = Potencia ajustada
                q_xx = Caudal total ajustado
*/
var HEAD_o = -1 * (Math.pow(10, -8)) * (Math.pow(q_xxo, 3)) + 8 * (Math.pow(10, 6)) * (Math.pow(q_xxo, 2)) - 0.0059 * q_xxo + 13.481;
var EFF_o = -8 * (Math.pow(10, -8)) * (Math.pow(q_xxo, 3)) - 7 * (Math.pow(10, 6)) * (Math.pow(q_xxo, 2)) + 0.14 * q_xxo + 6.6216;
var POWER_o = 6 * (Math.pow(10, 13)) * (Math.pow(q_xxo, 4)) - 2 * (Math.pow(10, 9)) * (Math.pow(q_xxo, 3)) + 2 * (Math.pow(10, -6)) * (Math.pow(q_xxo, 2)) + 0.0004 * q_xxo + 0.9563;

var HEAD = HEAD_o * (Math.pow(U_qaj , 2)) * 3.28 * N_etapas;
var q_xx = q_xxo / (0.159);
var POWER = POWER_o * (Math.pow(U_qaj , 3)) * N_etapas;

/*
    Paso No. 14: Se calcula el caudal y la eficiencia de la bomba
                q_xov = Caudal total ajustado con la viscosidad
                EF_bomba = Eficiencia de la bomba corregida vicosidad
*/
var q_xov = (qt / Cq) * U_qaj;
var EF_bomba = (Math.pow((frec / 60), 2)) * (((-5 * (Math.pow(10, -14)) * (Math.pow(q_xov, 4))) + (7 * (Math.pow(10, -10)) * (Math.pow(q_xov, 3))) - (5 * (Math.pow(10, -6)) * (Math.pow(q_xov, 2))) + ((0.23 * q_xov) - (0.1628)))/100);

/*
    Paso No. 15: Se calculan las áreas transversales de los tubos
                Area = Área de la tubería por encima de la bomba
                diam = Diametro de la tubería encima de la bomba
*/
var Area = 3.1416 * ((Math.pow(diam , 2)) / 4);  

/*
    Paso No. 16: Se convierte la viscosidad a unidades del sistema inglés
*/
var viscO = visc * (0.00067197); 

/*
    Paso No. 17: Velocidad del fluido, caudal (m3/s) y velocidad (ft/sec)
                Vf = Velocidad del fluido (m/s)
                q_fm = Caudal total en m3/s
                V_fm = Velocidad del fluido en ft/s
*/
var vf = (5.615 / (3600 * 24)) * (qt / Area);
var q_fm = parseInt(qt) / 543439.6505;
var V_fm = q_xx * (5.615 / 86400) / Area; 

/*
    Paso No. 18: Calculo del número de Reynols y factor de fricción
                re = Número de Reynols
                F_fric = Factor de fricción
*/
var re = diam * V_fm * (dens_mix / viscO);
var F_fric = 64 / re;

/*
    Paso No. 19: Calculo del gradiente de presión
*/
var VP = 0.433 * GE_mix;

/*
    Paso No. 20: Se convierte la presión en cabeza a ft con el gradiente y se estiman las pérdidas por fricción y gravitacional
                 THP_ft = Equivalencia en ft de la presión del tubing
                 p_fric = Pérdidas generadas por fricción
                 p_grav = Pérdidas por gravedad
                 l1 = Longitud del tubing por encima de la bomba
                 TDHH = Levantamiento en ft ajustado fricción
*/
var THP_ft = THP / VP;
var p_fric = (F_fric * dens_mix * ((Math.pow(V_fm, 2))/(2*32.2*diam))) / 144;
var p_grav = dens_mix / 144; 
var Caida_presion = (p_fric + p_grav) * l1;
var TDHH = (THP + Caida_presion) / VP;

/*
    Paso No. 21: CALCULOS ELECTRICOS
                POT_SUT = Potencia del SUT (Hp)
                POT_Cable = Potencia del clable (Hp)
                V_motor = Voltaje
                A_motor	= Corriente
                FP = Factor de potencia
                p_VoltCable = Pérdida en voltios en el cable
                p_Cable = Pérdidas en el cable (Hp)
*/
var POT_SUT = 1.73 * V_motor * A_motor * (FP / 745.6);
var POT_Cable = (V_motor - (15 / 1000) * (lcable)) * A_motor * 1.73 * (FP / 745.69);
var p_VoltCable = Prof_Sensor * 0.01;
var p_Cable = POT_SUT - POT_Cable;

/*
    Paso No. 22: CALCULOS ELECTRICOS. Se calcula el porcentaje de carga, caudal ajustado y la eficiencia
                porc_car = Porcentaje de carga
                EF = Eficiencia calculada 
*/
var porc_car = (POT_Cable / POT_Placa) * 100;
var EF = (-8 * (Math.pow(10, -8)) * (Math.pow(q_xxo, 3)) - 7 * (Math.pow(10, -6)) * (Math.pow(q_xxo, 2)) + 0.14  * (q_xxo) + 6.6216) / 100 ;
//var EF = 0.3273; 

/*
    Paso No. 23: CALCULOS ELECTRICOS.
                EF_motor = Eficiencia del motor
                POT_motor = Potencia del motor  
                p_motor = Pérdida del motor
                POT_sello = Potencia en el sello
                N_sellos = Número de sellos
                p_sello = Pérdida en el sello
                POT_bomba = Potencia en la bomba
                p_bomba = Pérdida en la bomba
                POT_real = Potencia real a partir de las pérdidas
                POT_realC = Potencia real calculada (Para graficar)
                POT_RCaj = Potencia real calculada ajustada frecuencia
*/
var EF_motor = (-2 * (Math.pow(10, -7)) * (Math.pow(porc_car, 4)) + 1 * (Math.pow(10, -4)) * (Math.pow(porc_car, 3)) - 0.0187 * (Math.pow(porc_car, 2)) + 1.4594 * (porc_car) + 38.618 ) / 100;
var POT_motor = POT_Cable * EF_motor;
var p_motor = POT_Cable - POT_motor;
var POT_sello = POT_motor - (4 * N_sellos);
var p_sello = POT_motor - POT_sello;
var POT_bomba = POT_sello * EF;
var p_bomba = POT_sello - POT_bomba;
var POT_real = POT_SUT - (p_Cable + p_motor + p_sello + p_bomba);
var POT_realC = 6 * (Math.pow(10, -13)) * (Math.pow(q_xxo, 4)) - 2 * (Math.pow(10, -9)) * (Math.pow(q_xxo, 3)) + 2 * (Math.pow(10, -6)) * (Math.pow(q_xxo, 2)) + 4 * (Math.pow(10, -4)) * (q_xxo) + 0.9563;
var POT_RCaj = POT_realC * (Math.pow(U_qaj, 3)) * N_etapas;

/*
    Paso No. 24: VARIABLES INFERIDAD POR POTENCIA
                v_PIP = Levantamiento del fluido a partir de la potencia
                v_PIPa = Levantamiento del fluido a partir de la potencia ajustado frecuencia
                PIP_inf = Presión en la bomba inferida
                PIP_infc = Presión en la bomba inferida calculada
*/
var v_PIP = POT_real * (136000 / q_xx);
var v_PIPa = POT_RCaj * (136000 / q_xx);
var PIP_inf = (TDHH - v_PIP) * VP;
var PIP_infc = (HEAD - v_PIP) * VP;

/*
    Paso No. 25: VARIABLES INFERIDAD POR POTENCIA. Se calcula la sumergencia, nivel de fluido y la pwf inferidas y el error
                S_inf = Sumergencia de la bomba inferida
                NFD_inf = Nivel de fluido inferido
                h_intk = Profundidad de la bomba
                Pwf_inf = Presión de fondo fluyente inferida
                Porc_PIP_IPR 
                Porc_PIP_POT
*/
var S_inf = (PIP_inf - PCasing) / VP;
var NFD_inf = Prof_Intake - S_inf ;
var Pwf_inf = PIP_inf + (PMP - Prof_Intake) * VP;
//var Porc_PIP_IPR = (PI)
//var Porc_PIP_POT = 

/*
    Paso No. 26: VARIABLES INFERIDAD POR POTENCIA. Levantamiento efectivo del yacimiento
                He = Levantamiento efectivo del yacimiento
*/
var He = Pwf_inf / VP ;

/*
    Paso No. 27: VARIABLES INFERIDAD POR POTENCIA. Nivel dinámico desde la superficie
                Lneto = Nivel dinámico (levantamiento neto)
                HD = Sumergencia de la bomba
*/
var Lneto = PMP - He;
var HD = Prof_Intake - Lneto;

/*
    Paso No. 28: CALCULO DE FRICCIÓN SEGÚN HAZEN WILLIAM
                Ct = 120 - constante para tuberías nuevas o 90 para viejas
                ff = 0,0150
                F = Factor Hazen William
                FR = Fricción calculada
                P_desc = Presión de descarga
                TDH = Levantamiento neto total
*/
var ct = 120; 
var F = (2.083 * (Math.pow((100 / ct), 1.85)) * (Math.pow((q_xx / 34.5), 1.85))) / (Math.pow((diam * 12), 4.8655));
var FR = F * (l1 / 1000);
var P_desc = THP /VP ; 
var TDH = Lneto + FR + P_desc

/*
    Paso No. 29: CALCULO DE FRICCIÓN SEGÚN HAZEN WILLIAM. 
                PIP con IPR          
*/
//var PIP_IPR = Pwf_P - (VP * (PMP - Prof_Intake));

/*
    Paso No. 30: CALCULO DE FRICCIÓN SEGÚN HAZEN WILLIAM.
                PIP sin IPR
*/
//var PIP_sin_IPR = THP + (* hb + GE_g * 0.433 * (PMP - hb));

/*
    Paso No. 31: CALCULO DE FRICCIÓN SEGÚN HAZEN WILLIAM.
                POT_pump = Potencia de la bomba calculada (kW)
*/
var POT_pump = (V_motor * A_motor * EF_motor * 1.73 * FP) / 1000;

/*
    Paso No. 32: CALCULO DE FRICCIÓN SEGÚN HAZEN WILLIAM. 
                Curva de potencia
*/
var POT_c = 2 * (Math.pow(10, -14)) * (Math.pow(qt, 4)) - 4 * (Math.pow(10, -10)) * (Math.pow(qt, 3)) + 2 * (Math.pow(10, -6)) * (Math.pow(qt, 2)) + 0.0018 * qt + 49.04;

/*
    Paso No. 33: CALCULO DE FRICCIÓN SEGÚN HAZEN WILLIAM.
                Eficiencia, head dinamico y carga dinámica
*/
var EFF = (Math.pow((frec / 60), 2)) * (-5 * (Math.pow(10, -14)) * (Math.pow(qt, 4)) + 7 * (Math.pow(10, -10)) * (Math.pow(qt, 3)) - 5 * (Math.pow(10, -6)) * (Math.pow(qt, 2)) + 0.023 * (qt) - 0.1628);
var Qt = qt * Bo;
var HEAD_din = (Math.pow((frec / 60), 2)) * (-7 * (Math.pow(10, -13)) * (Math.pow(Qt, 4)) + 8 * (Math.pow(10, -9)) * (Math.pow(Qt, 3)) - 3 * (Math.pow(10, -5)) * (Math.pow(Qt, 2)) - 0.0187 * Qt + 1792);
var Ho = (Math.pow((frec / 60), 2)) * (-7 * (Math.pow(10, -13)) * (Math.pow(q_xov, 4)) + 8 * (Math.pow(10, -9)) * (Math.pow(q_xov, 3)) - 3 * (Math.pow(10, -5)) * (Math.pow(q_xov, 2)) - 0.0187 * q_xov + 1792);


//Data de los Resultados
