}

var Output_Frequency    = global.get("C2S.Implicit.Output_Frequency",'memoryOnly')||0;
var Output_Current = global.get("C2S.Implicit.Output_Current", 'memoryOnly') || 0;
var Rtx = global.get("C2S.RelTx") || 1;
var MtrCurrent = Output_Current / Rtx;

return[
    { payload: catcherror(Output_Frequency,3)+ " " + "Hz"},
    { payload: catcherror(MtrCurrent,1) + " " + "A" },
]

function catcherror(variable,dec){
    return parseFloat(variable.toFixed(dec));
}

var i;
var datad = flow.get("amperimetric.dataday.data")||null;
var datadlabel = flow.get("amperimetric.dataday.label") || null;
var datawlabel = flow.get("amperimetric.dataweek.label") || null;
var dataw = flow.get("amperimetric.dataweek.data") || null;
var Output_Current = global.get("C2S.Implicit.Output_Current", 'memoryOnly') || 0;
var Rtx = global.get("C2S.RelTx") || 1;
var MtrCurrent = parseFloat((Output_Current / Rtx).toFixed(1));
var pos;
if(datad==null){
    datad=[null];
    datad [239] = null;
    datadlabel = [null];
    datadlabel[239] = null;
    flow.set("amperimetric.dataday.data",datad);
    for(i=0;i<datadlabel.length;i++){
        if((i==0)||(i % 10 ==0)){
            datadlabel[i]= (i/10)<10?"0"+(i/10)+":00":(i/10)+":00";
        }
        else datadlabel[i]="";
    }
    flow.set("amperimetric.dataday.label", datadlabel);
}
else{
    pos = ampSetValue()
    if(pos!=-1){
        datad[pos] = MtrCurrent;
        flow.set("amperimetric.dataday.data", datad);
    }
    if (((pos % 7 == 0) || (pos == 0)) && (dataw == null)) { flow.set("amperimetric.dataweek.data", [MtrCurrent]); flow.set("amperimetric.dataweek.label", ["-168 h"])}
    if (((pos % 7 == 0) || (pos == 0)) && (dataw != null)){
        dataw.push(MtrCurrent);
        if (datawlabel.length<240){
            if (datawlabel.length % 30 == 0) datawlabel.push("-" + (168 - ((datawlabel.length/30)*21))+" h");
            else datawlabel.push("");
            flow.set("amperimetric.dataweek.label", datawlabel)
        }
        if(dataw.length==241) dataw.shift();
        if (datawlabel.length == 241) datawlabel.shift();
        flow.set("amperimetric.dataweek.data", dataw);
    }
}
function ampSetValue(){
    var aux;
    var DT = new Date();
    var h = DT.getHours();
    var m = DT.getMinutes();
    if(m==0 && h==0)aux=0;
    m % 6 ==0 ? aux = ((h*60+m)/6):aux = -1;
    return aux;
}

