// Estado de protecciones
/**
* @param {{ HH: any; LL: any; H: any; L: any; }} Protection
*/
function protSts(Protection) {
    var Resp = 0;
    if (Protection.HH) { Resp = 4 }
    else if (Protection.LL) { Resp = 3 }
    else if (Protection.H) { Resp = 2 }
    else if (Protection.L) { Resp = 1 }
    else { Resp = 0 }
    return Resp;
}

